export const sendConfirmation = async ({
	email,
	event,
	name,
	tickets,
	categories,
}: {
	tickets: Database["public"]["Tables"]["tickets"]["Row"][];
	event: Database["public"]["Tables"]["events"]["Row"];
	email: string;
	name: string;
	categories: Database["public"]["Tables"]["ticket_categories"]["Row"][];
}) => {
	const pdfs: Uint8Array[] = [];

	const imageMapper = (id: number) => {
		return logos[id as unknown as "5" | "6" | "7" | "8"];
	};

	const categoryMapper = (id: number) => {
		const category = categories.find((c) => c.id === id);
		return category?.name || "Normal";
	};

	for (let i = 0; i < tickets.length; i++) {
		const inputs = [
			{
				title: `Tickets fr das Spiel am ${new Date(
					event.start_time,
				).toLocaleString("de-DE", {
					dateStyle: "full",
					timeStyle: "short",
					timeZone: "Europe/Berlin",
				})} Uhr`,
				event: `${event.name} (${VENUE})`,
				location: `Ticketkategorie: ${categoryMapper(tickets[i].ticket_category)}`,
				info: "Bitte zeigen Sie diesen QR-Code beim Einlass vor.\nErmigungen sind nur mit gltigem Ausweis gltig\n\nAGB: https://red-sparrows.getnono.de/agb\nDatenschutz: https://red-sparrows.getnono.de/impressum\nImpressum: https://red-sparrows.getnono.de/impressum",
				qr: tickets[i].scan_id || "",
				"code-text": tickets[i].scan_id || "",
				"sg-logo": logo,
				// edeka: edeka,
				// klaus: klaus,
				// sparkasse: sparkasse,
				// vgw: vgw,
				// bauverein: bauverein,
				// baur: baur,
				// gameImage: imageMapper(5),
			},
		];

		pdfs.push(
			await generate({
				inputs,
				template,
				plugins: {
					text,
					qrcode: barcodes.qrcode,
					image,
				},
			}),
		);
	}

	const pdfDoc = await PDFDocument.create();
	for (const pdf of pdfs) {
		const pdfBytes = await PDFDocument.load(pdf);
		const copiedPages = await pdfDoc.copyPages(
			pdfBytes,
			pdfBytes.getPageIndices(),
		);
		for (const page of copiedPages) {
			pdfDoc.addPage(page);
		}
	}
	const pdfBytes = await pdfDoc.saveAsBase64();

	const { data, error } = await resend.emails.send({
		from: "Ticketshop Red Sparrows <red-sparrows-tickets@getnono.app>",
		to: [email],
		subject: "Ihre Tickets",
		react: EmailTemplate({
			name,
			event_name: event.name,
			location: VENUE,
			start_time: event.start_time,
		}),
		html: `Hallo, ${name}!\n\nSie haben Tickets fr das Event ${event.name} am ${new Date(event.start_time).toLocaleString("de-De", { dateStyle: "full", timeStyle: "short" })} gekauft.\nOrt: ${VENUE}\nWir freuen uns darauf, Sie dort zu sehen!\nBitte beachten Sie, dass das Ticket in Form eines QR Codes vorliegt. Diesen knnen Sie an der Veranstaltung vorzeigen.\nZudem bentigen Sie einen gltigen Lichtbildausweis fr ermigte Tickets.`,
		attachments: [
			{
				content: pdfBytes,
				filename: "tickets.pdf",
			},
		],
	});
	if (error) {
		return Response.json({ error });
	}

	return Response.json(data);
};


